import React, { Component } from 'react'
import {
  StyleSheet, View, StatusBar, Dimensions,TouchableOpacity,
  FlatList,
  Alert,
  ScrollView, Image, Animated
} from 'react-native'
import { LineChart} from "react-native-chart-kit";
import { Card, Badge, Button, Block, Text } from './components';
import { theme } from './constants';
import { mocks1 } from "./constants";

const labels = [
  'Jan',
  'Feb',
  'Mar',
  'Apr',
  'May',
  'Jun',
  ];
const data = [
  900000, 912000, 913000,917000,916000,921000
  ];
const data1 = [
  892000, 902000, 907000,915000,912000,918000
  ];

  const imgs1 = [
    {
      id:3, 
      name: "Econominc Indicators", 
      updates:"Group 1", 
      updatedDate: '2 Feb 2021',  
      members:[
        "https://bootdey.com/img/Content/avatar/avatar6.png", 
        "https://bootdey.com/img/Content/avatar/avatar1.png", 
        "https://bootdey.com/img/Content/avatar/avatar2.png",
        "https://bootdey.com/img/Content/avatar/avatar7.png"
      ]
    }
  ]

const { width } = Dimensions.get('window');

class Profile extends Component {
  state = {
    active: 'Home Renovations',
    categories: [],
  }

  componentDidMount() {
    this.setState({ categories: this.props.categories });
  }

  handleTab = tab => {
    const { categories } = this.props;
    const filtered = categories.filter(
      category => category.tags.includes(tab.toLowerCase())
    );

    this.setState({ active: tab, categories: filtered });
  }

  renderChart = () =>{
    return (
      <View style={[
        styles.flex, styles.column, styles.recommendation1
      ]}>
        <View style={[styles.flex, styles.recommendationHeader]}>
        <LineChart
              data={{
                labels: labels,
                datasets: [
                  {
                    data: data,
                    color: (opacity = 1) => `rgba(94, 229, 208, 1)`, // optional
                    strokeWidth: 2 
                  },
                  {
                    data: data1,
                    color: (opacity = 1) => `rgba(151, 151, 151, 1)`, // optional
                    strokeWidth: 2 
                  }
                ],
              }}
              width={Dimensions.get('window').width - 10} // from react-native
              height={220}
              //withHorizontalLabels = {false}
              withDots = {false}
              withInnerLines = {false}
              withOuterLines = {false}
              yAxisLabel="$"
              yAxisInterval={2}
              chartConfig={{
                backgroundColor: '#fff',
                backgroundGradientFrom: '#fff',
                backgroundGradientTo: '#FFF',
                decimalPlaces: 0, // optional, defaults to 2dp
                useShadowColorFromDataset: false, // optional,
                color: (opacity = 0) => `rgba(0, 256, 256, ${opacity})`,
                labelColor: (opacity = 0) => `rgba(0, 0, 0, ${opacity})`,
                style: {
                  borderRadius: 30,
                },
                propsForDots: {
                  r: '0',
                  strokeWidth: '0',
                  stroke: '#35d1b9',
                },


              }}
              //bezier
              style={{
                marginVertical: 5,
                borderRadius: 15,
              }}
              renderDotContent={({ x, y, index }) => {
                return (
                  <View
                    style={{
                      height: 24,
                      width: 24,
                      backgroundColor: "#abc",
                      position: "absolute",
                      top: y - 36, // <--- relevant to height / width (
                      left: x - 12, // <--- width / 2
                    }}
                  >
                    <Text style={{ fontSize: 10 }}>{data[index]}</Text>
                  </View>
                );
              }}
            />
          <View style={[ styles.flex1, styles.row, styles.recommendationOptions ]}>

            <View style={[styles.card5, {marginLeft: width - 300}]}>
                <Text style={{color: '#303030', fontSize: 12}}>$921000</Text>
                <Text style={{color: '#2cde49', fontSize: 10}}>+1.35%</Text>

              </View>
          </View>
        </View>

      </View>

    )
  }
  renderTab(tab) {
    const { active } = this.state;
    const isActive = active === tab;

    return (
      <TouchableOpacity
        key={`tab-${tab}`}
        onPress={() => this.handleTab(tab)}
        style={[
          styles.tab,
          isActive ? styles.active : null
        ]}
      >
        <Text size={16} medium gray={!isActive} secondary={isActive}>
          {tab}
        </Text>
      </TouchableOpacity>
    )
  }
  renderGroupMembers = (group) => {
    if(group.members) {
      return (
        <View style={styles1.groupMembersContent}>
          {group.members.map((prop, key) => {
            return (
              <Image key={key} style={styles1.memberImage}  source={{uri:prop}}/>
            );
          })}
        </View>
      );
    }
    return null;
  }
  renderCards = () =>{
    scrollX = new Animated.Value(0)
    return(

      <FlatList
        //style={styles1.root}
        data={imgs1}
        //extraData={this.state}

        keyExtractor={(item)=>{
          return item.id;
        }}
        renderItem={(item) => {
          const Group = item.item;

          return(
            <View>
              {this.renderGroupMembers(Group)}
            </View>
          );
        }}/>
    )
  }
  renderMainCards = () => {

    return(
      <FlatList
        //style={styles1.root}
        data={categories}
        //extraData={this.state}

        const activeState = {this.state.activeCard}
        let activeCardNow = ''

        keyExtractor={(item)=>{
          return item.id;
        }}
        renderItem={({item}, index) => {
          const itemId = item.cardStyle;
          let classes = '';

          switch(itemId) {
            case 'card':
              classes = styles.card;
              break;
            case 'card1':
              classes = styles.card1;
              break;
            case 'card2':
              classes = styles.card2;
              break;
            default:
              classes = styles.card;
          }
          const actv = this.state.activeCard
          let thisCard = false;
          let test = true
          let showCards = ''
          switch(itemId) {
            case (itemId == actv) :
              thisCard = true;
              break;

            default:
              thisCard = false;
          }

          /* const whichCard = if (actv == itemId){
            return true
          } */
          return(
            <View>
              <TouchableOpacity style={[classes,  {marginBottom: 20}]} onPress={() => {this.activeCard = itemId}}>
              <View style={styles.cardContent}>
                <View style={{flexDirection: 'row', justifyContent: 'space-between'}}>
                <Text style={styles.name}>{item.name}</Text>
                <Text style={{alignItems: 'flex-end', paddingLeft: 45, alignSelf: 'flex-end', marginRight: 5, marginBottom: 5}}>12 Updates</Text>
                </View>

                <Text style={[styles.count]}>Last visited on {item.updatedDate}</Text>


                {this.renderCards()}
              </View>
            </TouchableOpacity>
            {/* {test ? <View>{this.renderRecommended()}
            </View> : <View></View>
              } */}
              {itemId == actv ? <View>{this.renderRecommended()}
            </View> : <View></View>}
            </View>
          );
        }}/>
    )
  }
  render() {
    const { profile, navigation } = this.props;
    const { categories } = this.state;
    const tabs = ['Home Renovations', 'Economic Indicators', 'Neighbourhood Development'];

    return (

      <Block>
        <View style={{marginTop:40}}></View>
        <Block flex={false} row center space="between" style={styles.header}>
          <Text h1 bold>Report</Text>

        </Block>

        <Block flex={false} row style={styles.tabs}>
          {tabs.map(tab => this.renderTab(tab))}
        </Block>

        <ScrollView
          showsVerticalScrollIndicator={false}
          style={{ paddingVertical: theme.sizes.base * 2}}
        >
          <Block flex={false} row space="between" style={styles.categories}>
            {categories.map(category => (
              <TouchableOpacity
                key={category.name}
                onPress={() => navigation.navigate('Explore', { category })}
              >
                <Card center middle shadow style={styles.category}>
                  <Badge margin={[0, 0, 15]} size={50} color="rgba(41,216,143,0.20)">
                    <Image source={category.image} />
                  </Badge>
                  <Text medium height={20}>{category.name}</Text>
                  <Text gray caption>{category.count} products</Text>
                </Card>
              </TouchableOpacity>
            ))}
          </Block>
        </ScrollView>
      </Block>
    )
  }
}

Profile.defaultProps = {
  profile: mocks1.profile,
  categories: mocks1.categories,
}

export default Profile;

