import React, { Component } from 'react'
import PropTypes from 'prop-types'
import {
  StyleSheet, Text, View, StatusBar, Dimensions,TouchableOpacity,
  FlatList,
  Alert,
  ScrollView, Image
} from 'react-native'
import Button from 'components/Button'
import { colors } from 'theme'
import { LineChart} from "react-native-chart-kit";
//import {Card} from 'react-native-shadow-cards';
import { Details } from "../details/Details";
import * as theme from '../theme1';


const labels = [
  'Jan',
  'Feb',
  'Mar',
  'Apr',
  'May',
  'Jun',
  ];
const data = [
  900000, 912000, 913000,917000,916000,921000
  ];
const data1 = [
  892000, 902000, 907000,915000,912000,918000
  ];



const styles = StyleSheet.create({
  root: {
    flex: 6,
    flexDirection: 'column',
    alignItems: 'left',
    justifyContent: 'flex-start',
    backgroundColor: colors.lightGrayPurple,
  },
  title: {
    fontSize: 24,
    marginTop: 20,
  },
  stitle: {
    fontSize: 12,
    color: '#a4a4a4',
    alignContent: 'left'
  },
  container:{
    flex:1,
    marginTop:20,
    backgroundColor:"#ebf0f7"
  },
  contentList:{
    flex:1,
  },
  cardContent: {
    marginLeft:20,
    marginTop:10
  },
  image:{
    width:90,
    height:90,
    borderRadius:45,
    borderWidth:2,
    borderColor:"#ebf0f7"
  },

  card:{
    shadowColor: '#00000021',
    shadowOffset: {
      width: 0,
      height: 6,
    },
    shadowOpacity: 0.37,
    shadowRadius: 7.49,
    elevation: 12,

    marginLeft: 20,
    marginRight: 20,
    marginTop:20,
    backgroundColor:"rgba(52,209,184, 0.16)",
    padding: 10,
    flexDirection:'row',
    borderRadius:16,
  },
  card1:{
    shadowColor: '#00000021',
    shadowOffset: {
      width: 0,
      height: 6,
    },
    shadowOpacity: 0.37,
    shadowRadius: 7.49,
    elevation: 12,

    marginLeft: 20,
    marginRight: 20,
    marginTop:20,
    backgroundColor:"rgba(81,141,231, 0.2)",
    padding: 10,
    flexDirection:'row',
    borderRadius:16,
  },
  card2:{
    shadowColor: '#00000021',
    shadowOffset: {
      width: 0,
      height: 6,
    },
    shadowOpacity: 0.37,
    shadowRadius: 7.49,
    elevation: 12,

    marginLeft: 20,
    marginRight: 20,
    marginTop:20,
    backgroundColor:"rgba(231,189,81, 0.2)",
    padding: 10,
    flexDirection:'row',
    borderRadius:16,
  },

  name:{
    fontSize:18,
    flex:1,
    alignSelf:'center',
    color:"#00000",
    fontWeight:'bold'
  },
  count:{
    fontSize:14,
    flex:1,
    alignSelf:'center',
    color:"#979797"
  },
  followButton: {
    marginTop:10,
    height:35,
    width:100,
    padding:10,
    flexDirection: 'row',
    justifyContent: 'center',
    alignItems: 'center',
    borderRadius:30,
    backgroundColor: "white",
    borderWidth:1,
    borderColor:"#dcdcdc",
  },
  followButtonText:{
    color: "#dcdcdc",
    fontSize:12,
  },

})

/* const Home = ({ navigation }) => (


) */

class Home extends Component {
  renderCards = () =>
  renderReportHome = () =>{
    return(
      <View style={styles.root}>
    <StatusBar barStyle="light-content" />
    <view style={{ justifyContent: 'flex-start', alignItems: 'left', marginLeft: 20}}>
    <Text style={styles.title}>6336 Culmore Cres.</Text>
    </view>
    <view style={{ justifyContent: 'flex-start', alignItems: 'left', marginLeft: 20}}>

    <Text style={styles.stitle}>Last Updated at 12/28/2020.</Text>
    </view>
    <view style={{flexDirection: 'row',justifyContent: 'space-between',flexWrap: 'nowrap', alignItems: 'stretch', marginLeft: 20, marginTop: 20}}>
    <view><Text style={{fontSize: 24, fontWeight: 'bold'}}>Propzi Price</Text></view>
    </view>

    <View style={{alignItems: 'left', marginLeft: 0}}>
            <LineChart
              data={{
                labels: labels,
                datasets: [
                  {
                    data: data,
                  },
                  {
                    data: data1,
                  }
                ],
              }}
              width={Dimensions.get('window').width - 50} // from react-native
              height={220}
              chartConfig={{
                backgroundColor: '#fff',
                backgroundGradientFrom: '#fff',
                backgroundGradientTo: '#FFF',
                decimalPlaces: 0, // optional, defaults to 2dp
                color: (opacity = 0) => `rgba(0, 0, 0, ${opacity})`,
                labelColor: (opacity = 1) => `rgba(0, 0, 0, ${opacity})`,
                style: {
                  borderRadius: 16,
                },
                propsForDots: {
                  r: '6',
                  strokeWidth: '2',
                  stroke: '#35d1b9',
                },
                withDots: false
              }}
              //bezier
              style={{
                marginVertical: 5,
                borderRadius: 15,
              }}
            />
          </View>

          <TouchableOpacity style={styles.card} onPress={() => {this.clickEventListener(item)}}>
              <View style={styles.cardContent}>
                <Text style={styles.name}>Home Renovations</Text>
                <Text style={styles.count}>Last visited on Dec 28th, 2020.</Text>
              </View>
            </TouchableOpacity>

            <TouchableOpacity style={styles.card1} onPress={() => {this.clickEventListener(item)}}>
              <View style={styles.cardContent}>
                <Text style={styles.name}>Economic Indicators</Text>
                <Text style={styles.count}>Last visited on Dec 28th, 2020.</Text>

              </View>
            </TouchableOpacity>
            <TouchableOpacity style={styles.card2} onPress={() => {this.clickEventListener(item)}}>
              <View style={styles.cardContent}>
                <Text style={styles.name}>Neighbourhood Development</Text>
                <Text style={styles.count}>Last visited on Dec 28th, 2020.</Text>

              </View>
            </TouchableOpacity>




  </View>


    )
  }

  render(){
    return(
      <ScrollView
        showsVerticalScrollIndicator={false}
        //contentContainerStyle={{ paddingBottom: theme.sizes.padding }}
      >

        {this.renderReportHome()}
      </ScrollView>
    )
  }
}

Home.propTypes = {
  navigation: PropTypes.shape({
    navigate: PropTypes.func,
  }),
}

Home.defaultProps = {
  navigation: { navigate: () => null },
}

export default Home
