import Button from 'components/Button'
import { colors } from 'theme'
import { LineChart} from "react-native-chart-kit";
//import {Card} from 'react-native-shadow-cards';
import { Details } from "../details/Details";
import * as theme from '../theme1';


const labels = [
  'Jan',
  'Feb',
  'Mar',
  'Apr',
  'May',
  'Jun',
  ];
const data = [
  900000, 912000, 913000,917000,916000,921000
  ];
const data1 = [
  892000, 902000, 907000,915000,912000,918000
  ];



const styles = StyleSheet.create({
  root: {
    flex: 1,
    flexDirection: 'column',
    alignItems: 'left',
    justifyContent: 'flex-start',
    backgroundColor: colors.lightGrayPurple,
  },
  title: {
    fontSize: 24,
    marginTop: 20,
  },
  stitle: {
    fontSize: 12,
    color: '#a4a4a4',
    alignContent: 'left'
  },
  container:{
    flex:1,
    marginTop:20,
    backgroundColor:"#ebf0f7"
  },
  contentList:{
    flex:1,
  },
  cardContent: {
    marginLeft:20,
    marginTop:10
  },
  image:{
    width:90,
    height:90,
    borderRadius:45,
    borderWidth:2,
    borderColor:"#ebf0f7"
  },

  card:{
    shadowColor: '#00000021',
    shadowOffset: {
      width: 0,
      height: 6,
    },
    shadowOpacity: 0.37,
    shadowRadius: 7.49,
    elevation: 12,

    marginLeft: 20,
    marginRight: 20,
    marginTop:20,
    backgroundColor:"rgba(52,209,184, 0.16)",
    padding: 10,
    flexDirection:'row',
    borderRadius:16,
  },
  card1:{
    shadowColor: '#00000021',
    shadowOffset: {
      width: 0,
      height: 6,
    },
    shadowOpacity: 0.37,
    shadowRadius: 7.49,
    elevation: 12,

    marginLeft: 20,
    marginRight: 20,
    marginTop:20,
    backgroundColor:"rgba(81,141,231, 0.2)",
    padding: 10,
    flexDirection:'row',
    borderRadius:16,
  },
  card2:{
    shadowColor: '#00000021',
    shadowOffset: {
      width: 0,
      height: 6,
    },
    shadowOpacity: 0.37,
    shadowRadius: 7.49,
    elevation: 12,

    marginLeft: 20,
    marginRight: 20,
    marginTop:20,
    backgroundColor:"rgba(231,189,81, 0.2)",
    padding: 10,
    flexDirection:'row',
    borderRadius:16,
  },

  name:{
    fontSize:18,
    flex:1,
    alignSelf:'center',
    color:"#00000",
    fontWeight:'bold'
  },
  count:{
    fontSize:14,
    flex:1,
    alignSelf:'center',
    color:"#979797"
  },
  followButton: {
    marginTop:10,
    height:35,
    width:100,
    padding:10,
    flexDirection: 'row',
    justifyContent: 'center',
    alignItems: 'center',
    borderRadius:30,
    backgroundColor: "white",
    borderWidth:1,
    borderColor:"#dcdcdc",
  },
  followButtonText:{
    color: "#dcdcdc",
    fontSize:12,
  },
  flex: {
    flex: 1,
  },
  flex1: {
    flex: 1,
    alignItems: 'flex-end'
  },
  column: {
    flexDirection: 'column'
  },
  row: {
    flexDirection: 'row'
  },

  recommended: {
  },
  recommendedHeader: {
    justifyContent: 'space-between',
    alignItems: 'flex-end',
    paddingHorizontal: theme.sizes.padding,
    marginVertical: theme.sizes.margin * 0.66
  },
  recommendedList: {
  },
  recommendation: {
    width: (width - (theme.sizes.padding * 2)) / 1.2,
    marginHorizontal: 8,
    backgroundColor: '#f6f6f6',
    overflow: 'hidden',
    borderTopRightRadius: theme.sizes.border,
    borderTopLeftRadius: theme.sizes.border,
    borderBottomRightRadius: 16,
    borderBottomLeftRadius: 16
  },
  recommendationHeader: {
    overflow: 'hidden',
    borderTopRightRadius: theme.sizes.border,
    borderTopLeftRadius: theme.sizes.border,
  },
  recommendationOptions: {
    alignItems: 'center',
    justifyContent: 'space-between',
    padding: theme.sizes.padding / 2,
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
  },
  recommendationTemp: {
    fontSize: theme.sizes.font * 1.25,
    color: theme.colors.white
  },
  recommendationImage: {
    width: (width - (theme.sizes.padding * 2)) / 1.2,
    height: (width - (theme.sizes.padding * 2)) / 2,
  },
  avatar: {
    width: theme.sizes.padding,
    height: theme.sizes.padding,
    borderRadius: theme.sizes.padding / 2,
  },

  shadow: {
    shadowColor: theme.colors.black,
    shadowOffset: {
      width: 0,
      height: 6,
    },
    shadowOpacity: 0.05,
    shadowRadius: 10,
  },

  card3:{
    shadowColor: '#00000021',
    shadowOffset: {
      width: 0,
      height: 6,
    },
    shadowOpacity: 0.37,
    shadowRadius: 7.49,
    elevation: 12,

    /* marginLeft: 20,
    marginRight: 20, */
    backgroundColor:"rgba(52,209,184, 1)",
    padding: 5,
    flexDirection:'row',
    borderRadius:16,
    alignSelf: 'flex-end'
  },
  card4:{
    shadowColor: '#00000021',
    shadowOffset: {
      width: 0,
      height: 6,
    },
    shadowOpacity: 0.37,
    shadowRadius: 7.49,
    elevation: 12,

    /* marginLeft: 20,
    marginRight: 20, */
    backgroundColor:"#fff",
    padding: 5,
    flexDirection:'row',
    borderRadius:16,
    width: 140
  },
  count:{
    fontSize:14,
    alignSelf:'flex-start',
    color:"#1f2123"
  },
  count1:{
    fontSize:14,
    alignSelf:'flex-end',
    color:"#960303"
  },
})

/* const Home = ({ navigation }) => (


) */

class Home extends Component {
  renderCards = () =>{
    return(
      <Details></Details>
    )
  }
  renderReportHome = () =>{
    return(
      <View style={styles.root}>
    <StatusBar barStyle="light-content" />
    <view style={{ justifyContent: 'flex-start', alignItems: 'left', marginLeft: 20}}>
    <Text style={styles.title}>6336 Culmore Cres.</Text>
    </view>
    <view style={{ justifyContent: 'flex-start', alignItems: 'left', marginLeft: 20}}>

    <Text style={styles.stitle}>Last Updated at 12/28/2020.</Text>
    </view>
    <view style={{flexDirection: 'row',justifyContent: 'space-between',flexWrap: 'nowrap', alignItems: 'stretch', marginLeft: 20, marginTop: 20}}>
    <view><Text style={{fontSize: 24, fontWeight: 'bold'}}>Propzi Price</Text></view>
    </view>

    <View style={{alignItems: 'left', marginLeft: 0}}>
            <LineChart
              data={{
                labels: labels,
                datasets: [
                  {
                    data: data,
                  },
                  {
                    data: data1,
                  }
                ],
              }}
              width={Dimensions.get('window').width - 50} // from react-native
              height={220}
              chartConfig={{
                backgroundColor: '#fff',
                backgroundGradientFrom: '#fff',
                backgroundGradientTo: '#FFF',
                decimalPlaces: 0, // optional, defaults to 2dp
                color: (opacity = 0) => `rgba(0, 0, 0, ${opacity})`,
                labelColor: (opacity = 1) => `rgba(0, 0, 0, ${opacity})`,
                style: {
                  borderRadius: 16,
                },
                propsForDots: {
                  r: '6',
                  strokeWidth: '2',
                  stroke: '#35d1b9',
                },
                withDots: false
              }}
              //bezier
              style={{
                marginVertical: 5,
                borderRadius: 15,
              }}
            />
          </View>

          <TouchableOpacity style={styles.card} onPress={() => {this.clickEventListener(item)}}>
